CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    preferences JSONB
);

CREATE TABLE categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    customer_count INT
);

CREATE TABLE category_preferences (
    preference_id INT PRIMARY KEY,
    preference_name VARCHAR(50)
);

CREATE TABLE buying_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(50),
    purchase_frequency INT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    customer_email VARCHAR(255),
    customer_phone VARCHAR(20),
    customer_address TEXT
);

CREATE TABLE preferences (
    preference_id INT PRIMARY KEY,
    customer_id INT,
    preference_name VARCHAR(255),
    preference_category VARCHAR(50),
    preference_description TEXT
);

CREATE TABLE purchases (
    purchase_id INT PRIMARY KEY,
    customer_id INT,
    purchase_date DATE,
    total_amount DECIMAL(10, 2)
);

CREATE TABLE categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(100)
);
