tables.table_schema:
CREATE TABLE market_trends (
    trend_id INT PRIMARY KEY,
    crypto_id INT REFERENCES cryptocurrencies(id),
    trend_date DATE NOT NULL,
    trend_description VARCHAR(255) NOT NULL
);

CREATE TABLE cryptocurrencies (
  id INT PRIMARY KEY,
  name STRING,
  symbol STRING,
  market_cap FLOAT
);

CREATE TABLE market_analysis (
  id INT PRIMARY KEY,
  market_data_id INT REFERENCES market_data(id),
  trend STRING,
  momentum FLOAT
);

CREATE TABLE alerts (
  id INT PRIMARY KEY,
  market_analysis_id INT REFERENCES market_analysis(id),
  alert_type ENUM('buy', 'sell'),
  alert_message STRING
);

CREATE TABLE exchanges (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    location VARCHAR(50),
    rating FLOAT
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(50),
    password VARCHAR(50)
);
