tables.table_schema:
CREATE TABLE customer_payments (
    customer_id INT PRIMARY KEY,
    card_number VARCHAR(16),
    card_expiry_date DATE,
    payment_amount DECIMAL(10, 2)
);

CREATE TABLE CustomerDetails (
  customer_id INT PRIMARY KEY,
  customer_name TEXT,
  customer_email TEXT UNIQUE,
  customer_address TEXT
);

CREATE TABLE TransactionHistory (
  transaction_id INT PRIMARY KEY,
  customer_id INT REFERENCES CustomerDetails(customer_id),
  transaction_amount DECIMAL,
  transaction_date TIMESTAMP
);

CREATE TABLE customer_details (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    email_id VARCHAR(255) UNIQUE,
    phone_number VARCHAR(15)
);

CREATE TABLE payment_details (
    payment_id INT PRIMARY KEY,
    customer_id INT REFERENCES customer_details(customer_id),
    payment_amount DECIMAL(10, 2),
    payment_date TIMESTAMP
);

CREATE TABLE card_details (
    card_id INT PRIMARY KEY,
    customer_id INT REFERENCES customer_details(customer_id),
    card_number VARCHAR(16) UNIQUE,
    card_expiry_date DATE
);

CREATE TABLE transaction_details (
    transaction_id INT PRIMARY KEY,
    payment_id INT REFERENCES payment_details(payment_id),
    card_id INT REFERENCES card_details(card_id),
    transaction_date TIMESTAMP
);

CREATE TABLE customer (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    customer_email VARCHAR(100) UNIQUE,
    customer_address VARCHAR(150)
);

CREATE TABLE payment_method (
    method_id INT PRIMARY KEY,
    method_type VARCHAR(20) NOT NULL,
    method_description VARCHAR(100)
);

CREATE TABLE transaction (
    transaction_id INT PRIMARY KEY,
    payment_id INT,
    transaction_date TIMESTAMP,
    status VARCHAR(20)
);
