CREATE TABLE contactless_payment_auth ( 
  authentication_code UUID PRIMARY KEY, 
  transaction_id BIGINT NOT NULL, 
  authorized_amount DECIMAL(10, 2) NOT NULL, 
  authorization_timestamp TIMESTAMP NOT NULL 
);

CREATE TABLE authorization_codes (
  code_id INT PRIMARY KEY,
  authorization_code VARCHAR(50) NOT NULL,
  transaction_id INT NOT NULL,
  created_at TIMESTAMP
);

CREATE TABLE payment_authorization (
    authorization_code UUID PRIMARY KEY,
    user_id INT,
    transaction_id INT,
    authorization_time TIMESTAMP
);

