tables.table_schema:
CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    theme VARCHAR(20),
    language VARCHAR(20)
);

CREATE TABLE preferences (
    preference_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    theme VARCHAR(20),
    language VARCHAR(20)
);

CREATE TABLE PhoneNumbers (
    id INT PRIMARY KEY,
    user_id INT,
    phone_number TEXT,
    is_primary BOOLEAN
);

CREATE TABLE UserInterests (
    id INT PRIMARY KEY,
    user_id INT,
    interest TEXT
);
