tables.table_schema:
CREATE TABLE loan_applications (
    application_id INT PRIMARY KEY,
    customer_id INT,
    loan_amount DECIMAL,
    application_date DATE
);

CREATE TABLE loan_applications (
  id INT PRIMARY KEY,
  customer_id INT,
  amount DECIMAL,
  status VARCHAR(50)
);

CREATE TABLE mortgage_applications (
  id INT PRIMARY KEY,
  customer_id INT,
  property_address VARCHAR(255),
  loan_amount DECIMAL
);

CREATE TABLE customer_details (
  id INT PRIMARY KEY,
  name VARCHAR(100),
  email VARCHAR(100),
  phone_number VARCHAR(20)
);
