CREATE TABLE peer_details (
  peer_id SERIAL PRIMARY KEY,
  peer_name VARCHAR(255) NOT NULL,
  peer_address VARCHAR(255) NOT NULL
);

CREATE TABLE Peer (
  peer_id INT PRIMARY KEY,
  name VARCHAR(50),
  email VARCHAR(100),
  address VARCHAR(100)
);

CREATE TABLE peer (
    peer_id SERIAL PRIMARY KEY,
    peer_name VARCHAR(50) NOT NULL,
    registration_date TIMESTAMP NOT NULL,
    balance DECIMAL NOT NULL
);

CREATE TABLE transaction_status (
    status_id SERIAL PRIMARY KEY,
    transaction_id INT NOT NULL,
    status VARCHAR(20) NOT NULL,
    update_time TIMESTAMP NOT NULL
);

CREATE TABLE transaction_status (
  status_id SERIAL PRIMARY KEY,
  transaction_id INT NOT NULL,
  status TEXT NOT NULL,
  update_time TIMESTAMP NOT NULL
);

CREATE TABLE peer_accounts (
  account_id SERIAL PRIMARY KEY,
  peer_id INT NOT NULL,
  balance NUMERIC NOT NULL
);

CREATE TABLE peer_details (
  peer_id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  email TEXT NOT NULL UNIQUE
);

CREATE TABLE peer_relationship (
  relationship_id SERIAL PRIMARY KEY,
  peer1_id INT NOT NULL,
  peer2_id INT NOT NULL,
  relationship_type TEXT NOT NULL
);
