tables.table_schema:
CREATE TABLE fraud_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_description TEXT,
    fraud_type VARCHAR(50)
);

CREATE TABLE fraud_detection_users (
    id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE fraud_patterns (
    pattern_id SERIAL PRIMARY KEY,
    description TEXT,
    pattern_source TEXT
);

CREATE TABLE customers (
	customer_id INT PRIMARY KEY,
	customer_name VARCHAR(50),
	account_balance DECIMAL
);

CREATE TABLE transactions (
	transaction_id INT PRIMARY KEY,
	customer_id INT,
	transaction_amount DECIMAL,
	transaction_date TIMESTAMP
);

CREATE TABLE flagged_transactions (
	transaction_id INT PRIMARY KEY,
	customer_id INT,
	flag_reason VARCHAR(100),
	flagged_date TIMESTAMP
);

CREATE TABLE suspicious_activity (
	suspicious_id INT PRIMARY KEY,
	customer_id INT,
	suspicious_description VARCHAR(200),
	suspicious_date DATE
);

CREATE TABLE alerts (
	alert_id INT PRIMARY KEY,
	customer_id INT,
	alert_message VARCHAR(250),
	alert_date TIMESTAMP
);
