tables.table_schema:
CREATE TABLE loyalty_points (
    customer_id UUID,
    transaction_id UUID,
    points_earned INT,
    timestamp TIMESTAMP
);

CREATE TABLE LoyaltyPoints (
    customer_id INT PRIMARY KEY,
    transaction_date DATE,
    points_earned INT
);

CREATE TABLE CustomerInformation (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    email VARCHAR(255),
    loyalty_level INT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    customer_id INT,
    transaction_date DATE,
    transaction_amount DECIMAL
);

CREATE TABLE points_earned (
    earn_id INT PRIMARY KEY,
    customer_id INT,
    transaction_id INT,
    points_earned INT
);

CREATE TABLE loyal_customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    points_earned INT
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    customer_id INT,
    transaction_date DATE,
    transaction_amount INT
);

CREATE TABLE rewards (
    reward_id INT PRIMARY KEY,
    reward_name TEXT,
    points_required INT
);

CREATE TABLE points_history (
    transaction_id INT,
    customer_id INT,
    points_earned INT,
    points_spent INT
);

CREATE TABLE LoyaltyMembers (
	loyalty_id INT PRIMARY KEY,
	customer_name VARCHAR(50) NOT NULL,
	points_earned INT DEFAULT 0
);

CREATE TABLE Transactions (
	transaction_id INT PRIMARY KEY,
	customer_id INT NOT NULL,
	transaction_amount DECIMAL(10, 2) NOT NULL,
	transaction_date DATE
);

CREATE TABLE Rewards (
	reward_id INT PRIMARY KEY,
	reward_name VARCHAR(50) NOT NULL,
	required_points INT NOT NULL,
	reward_description TEXT
);

CREATE TABLE TransactionsToRewards (
	transaction_id INT NOT NULL,
	reward_id INT NOT NULL,
	FOREIGN KEY (transaction_id) REFERENCES Transactions(transaction_id),
	FOREIGN KEY (reward_id) REFERENCES Rewards(reward_id)
);

CREATE TABLE LoyaltyPointsHistory (
	history_id INT PRIMARY KEY,
	loyalty_id INT NOT NULL,
	points_change INT NOT NULL,
	history_date TIMESTAMP
);
