tables.table_schema:
CREATE TABLE fraud_detection (
  id INT PRIMARY KEY,
  transaction_id INT,
  fraud_score DECIMAL(5,2),
  flagged BOOLEAN
);

CREATE TABLE user_accounts (
    user_id INT PRIMARY KEY,
    account_number VARCHAR,
    balance DECIMAL,
    status VARCHAR
);

CREATE TABLE fraud_records (
    id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    description TEXT
);
