tables.table_schema:
CREATE TABLE sales_performance (
    report_id INT PRIMARY KEY,
    report_date DATE,
    revenue DECIMAL
);

CREATE TABLE business_decisions (
    decision_id INT PRIMARY KEY,
    decision_date DATE,
    decision_type VARCHAR(50),
    decision_details TEXT
);

CREATE TABLE sales_report (
    report_id SERIAL PRIMARY KEY,
    report_date DATE NOT NULL,
    total_sales DECIMAL NOT NULL,
    total_revenue DECIMAL NOT NULL
);

CREATE TABLE sales_person (
    person_id SERIAL PRIMARY KEY,
    person_name VARCHAR(50) NOT NULL,
    sales_region VARCHAR(50) NOT NULL
);

CREATE TABLE revenue_target (
    target_id SERIAL PRIMARY KEY,
    target_month VARCHAR(10) NOT NULL,
    target_amount DECIMAL NOT NULL
);

CREATE TABLE sales_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    total_sales DECIMAL,
    total_revenue DECIMAL
);

CREATE TABLE sales_team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    team_manager VARCHAR(50)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    unit_price DECIMAL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    customer_email VARCHAR(50)
);
