tables.table_schema:
CREATE TABLE authorized_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_amount DECIMAL,
    authorized_user_id INT,
    authorized_timestamp TIMESTAMP
);

CREATE TABLE unauthorized_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_amount DECIMAL,
    unauthorized_user_id INT,
    unauthorized_timestamp TIMESTAMP
);

CREATE TABLE suspicious_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_amount DECIMAL,
    suspicious_reason TEXT,
    identified_by_algorithm VARCHAR
);

CREATE TABLE accounts (
    account_id INT PRIMARY KEY,
    account_holder_name VARCHAR(255),
    account_type VARCHAR(50),
    balance DECIMAL
);

CREATE TABLE fraud_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_description VARCHAR(255),
    pattern_type VARCHAR(50)
);

CREATE TABLE transaction_logs (
    log_id INT PRIMARY KEY,
    transaction_id INT,
    log_timestamp TIMESTAMP,
    log_message VARCHAR(255)
);

CREATE TABLE accounts (
    account_id UUID PRIMARY KEY,
    account_number VARCHAR,
    account_balance DECIMAL,
    account_type VARCHAR
);

CREATE TABLE fraud_history (
    fraud_id UUID PRIMARY KEY,
    transaction_id UUID,
    fraud_status VARCHAR,
    fraud_date TIMESTAMP
);

CREATE TABLE auth_tokens (
    token_id UUID PRIMARY KEY,
    user_id UUID,
    token_value VARCHAR,
    expiration_date TIMESTAMP
);
