tables.table_schema:
CREATE TABLE Customers (
    customer_id INT,
    customer_name VARCHAR(255),
    email VARCHAR(255),
    subscription_plan VARCHAR(50)
);

CREATE TABLE subscription_plans (
    plan_id INT PRIMARY KEY,
    plan_name TEXT,
    monthly_price DECIMAL,
    features TEXT
);

CREATE TABLE payments (
    payment_id INT PRIMARY KEY,
    invoice_id INT,
    payment_date DATE,
    amount_paid DECIMAL
);
