tables.table_schema:
CREATE TABLE payment_gateways (
    id UUID PRIMARY KEY,
    gateway_name VARCHAR(50) NOT NULL,
    gateway_url VARCHAR(255) NOT NULL
);

CREATE TABLE credit_card_transactions (
    transaction_id SERIAL PRIMARY KEY,
    card_number VARCHAR(16) NOT NULL,
    card_holder_name VARCHAR(100) NOT NULL,
    transaction_amount DECIMAL(10,2) NOT NULL,
    transaction_date TIMESTAMP NOT NULL,
    gateway_id UUID NOT NULL REFERENCES payment_gateways(id)
);

CREATE TABLE transaction_logs (
    log_id SERIAL PRIMARY KEY,
    transaction_id SERIAL NOT NULL REFERENCES credit_card_transactions(transaction_id),
    log_message TEXT,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE payment_gateways (
   gateway_id INT PRIMARY KEY,
   gateway_name VARCHAR(50),
   api_key VARCHAR(100),
   endpoint_url VARCHAR(100)
);

CREATE TABLE payment_gateways (
    gateway_id INT PRIMARY KEY,
    gateway_name VARCHAR(255),
    integration_key VARCHAR(255),
    encryption_key VARCHAR(255)
);

CREATE TABLE cards (
    card_id INT PRIMARY KEY,
    card_number VARCHAR(16),
    customer_id INT,
    is_default BOOLEAN
);
