tables.table_schema:
CREATE TABLE UserProfiles (
    user_id UUID PRIMARY KEY,
    username TEXT,
    email TEXT,
    password TEXT
);

CREATE TABLE AccountInformation (
    account_id UUID PRIMARY KEY,
    user_id UUID,
    account_type TEXT,
    balance DECIMAL
);

CREATE TABLE BiometricData (
    user_id UUID,
    fingerprint_data BYTEA,
    face_data BYTEA
);
