tables.table_schema:
CREATE TABLE biometric_payment_transactions (
    transaction_id SERIAL PRIMARY KEY,
    user_id VARCHAR(50) NOT NULL,
    amount DECIMAL NOT NULL,
    transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE fraud_detection (
    fraud_id SERIAL PRIMARY KEY,
    transaction_id INT NOT NULL,
    fraud_status VARCHAR(20) NOT NULL,
    fraud_description TEXT
);

CREATE TABLE fraud_detection (
    transaction_id INT PRIMARY KEY,
    fraud_score DECIMAL(5,2),
    fraud_reason TEXT,
    fraud_status BOOLEAN
);

CREATE TABLE reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    analysis_result TEXT,
    summary TEXT
);

CREATE TABLE biometric_data (
  user_id INT NOT NULL,
  biometric_type VARCHAR(50) NOT NULL,
  biometric_data BLOB NOT NULL,
  transaction_time TIMESTAMP NOT NULL
);
