tables.table_schema:
CREATE TABLE qr_payments (
    payment_id UUID PRIMARY KEY,
    amount DECIMAL,
    qr_code TEXT,
    payment_status TEXT
);

CREATE TABLE confirmation_messages (
    message_id UUID PRIMARY KEY,
    payment_id UUID REFERENCES qr_payments(payment_id),
    message_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE qr_code_payments (
    id SERIAL PRIMARY KEY,
    qr_code_id UUID NOT NULL,
    amount DECIMAL NOT NULL,
    payment_status VARCHAR(50) NOT NULL
);

CREATE TABLE confirmation_messages (
    id SERIAL PRIMARY KEY,
    message_text TEXT NOT NULL,
    timestamp TIMESTAMP NOT NULL DEFAULT current_timestamp
);

CREATE TABLE payment_history (
    id SERIAL PRIMARY KEY,
    qr_code_id UUID NOT NULL,
    payment_amount DECIMAL NOT NULL,
    payment_timestamp TIMESTAMP NOT NULL DEFAULT current_timestamp
);

CREATE TABLE qr_codes (
    id INT PRIMARY KEY,
    qr_code VARCHAR(255) NOT NULL,
    payment_amount DECIMAL(10, 2) NOT NULL,
    payment_status BOOLEAN
);

CREATE TABLE confirmation_messages (
    id INT PRIMARY KEY,
    payment_id INT NOT NULL,
    message_body TEXT,
    sent_date TIMESTAMP
);

CREATE TABLE Payments (
  payment_id INT PRIMARY KEY,
  payment_amount DECIMAL,
  payment_date TIMESTAMP,
  qr_code_id VARCHAR
);

CREATE TABLE QRCodeMessages (
  qr_code_id VARCHAR PRIMARY KEY,
  confirmation_message TEXT
);

CREATE TABLE SuccessfulPayments (
  success_payment_id INT PRIMARY KEY,
  payment_id INT REFERENCES Payments(payment_id),
  qr_code_id VARCHAR REFERENCES Payments(qr_code_id),
  success_message TEXT
);

CREATE TABLE FailedPayments (
  failed_payment_id INT PRIMARY KEY,
  payment_id INT REFERENCES Payments(payment_id),
  failure_reason TEXT
);

CREATE TABLE PaymentHistory (
  history_id INT PRIMARY KEY,
  payment_id INT REFERENCES Payments(payment_id),
  status VARCHAR,
  time_stamp TIMESTAMP
);
