tables.table_schema:
CREATE TABLE sensitive_financial_data (
    id UUID PRIMARY KEY,
    account_number VARCHAR(50) ENCRYPTED,
    balance DECIMAL,
    owner_name VARCHAR(100) ENCRYPTED
);

CREATE TABLE access_control (
    user_id UUID PRIMARY KEY,
    role VARCHAR(50),
    access_level INT,
    granted BOOLEAN
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  credit_card_number VARCHAR(16),
  encrypted_ssn VARCHAR(50)
);

CREATE TABLE sensitive_customers (
  customer_id INT PRIMARY KEY,
  customer_name TEXT,
  encrypted_ssn BYTEA,
  encrypted_credit_card BYTEA
);

CREATE TABLE transactions (
  transaction_id INT PRIMARY KEY,
  customer_id INT,
  transaction_amount DECIMAL,
  transaction_date TIMESTAMP
);

CREATE TABLE employee_access (
  employee_id INT PRIMARY KEY,
  access_level INT,
  department TEXT,
  password_hash BYTEA
);

