CREATE TABLE participants (
    participant_id UUID PRIMARY KEY,
    participant_name VARCHAR(50),
    organization VARCHAR(50),
    role VARCHAR(50)
);

CREATE TABLE roles (
    role_id UUID PRIMARY KEY,
    role_name VARCHAR(50)
);

CREATE TABLE organizations (
    organization_id UUID PRIMARY KEY,
    organization_name VARCHAR(50)
);

CREATE TABLE blockchain_network_participants (
    participant_id UUID PRIMARY KEY,
    participant_name VARCHAR(100) NOT NULL,
    permission_level INT NOT NULL
);

CREATE TABLE permission_levels (
    level_id SERIAL PRIMARY KEY,
    level_name VARCHAR(50) NOT NULL UNIQUE
);

CREATE TABLE participant_permissions (
    permission_id SERIAL PRIMARY KEY,
    participant_id UUID REFERENCES blockchain_network_participants(participant_id),
    level_id INT REFERENCES permission_levels(level_id)
);

CREATE TABLE activities (
    activity_id SERIAL PRIMARY KEY,
    activity_name VARCHAR(100) NOT NULL,
    permission_level_required INT
);

CREATE TABLE activity_logs (
    log_id SERIAL PRIMARY KEY,
    activity_id INT REFERENCES activities(activity_id),
    participant_id UUID REFERENCES blockchain_network_participants(participant_id),
    timestamp TIMESTAMP
);
