CREATE TABLE backups (
    backup_id SERIAL PRIMARY KEY,
    backup_name VARCHAR(50) NOT NULL,
    backup_schedule TIMESTAMP NOT NULL,
    backup_location VARCHAR(100) NOT NULL
);

CREATE TABLE backup_logs (
    log_id SERIAL PRIMARY KEY,
    backup_id INT REFERENCES backups(backup_id),
    log_timestamp TIMESTAMP NOT NULL,
    log_message TEXT
);

CREATE TABLE backup_jobs (
    job_id SERIAL PRIMARY KEY,
    backup_id INT REFERENCES backups(backup_id),
    job_status VARCHAR(20) NOT NULL,
    job_details TEXT
);

CREATE TABLE backup_history (
    history_id SERIAL PRIMARY KEY,
    backup_id INT REFERENCES backups(backup_id),
    history_timestamp TIMESTAMP NOT NULL,
    history_status VARCHAR(20) NOT NULL
);

CREATE TABLE backup_config (
    id SERIAL PRIMARY KEY,
    backup_name VARCHAR(100) NOT NULL,
    backup_frequency INTERVAL NOT NULL,
    backup_location VARCHAR(255) NOT NULL
);

CREATE TABLE backup_history (
    backup_id INT REFERENCES backup_config(id),
    backup_time TIMESTAMP NOT NULL,
    backup_size DOUBLE PRECISION NOT NULL
);

CREATE TABLE backup_logs (
    log_id SERIAL PRIMARY KEY,
    backup_id INT REFERENCES backup_config(id),
    log_message TEXT,
    log_time TIMESTAMP NOT NULL
);

CREATE TABLE backup_users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password VARCHAR(100) NOT NULL
);
