tables.table_schema:
CREATE TABLE organization ( 
    id INT PRIMARY KEY, 
    name VARCHAR(50), 
    email VARCHAR(100), 
    phone VARCHAR(20) 
);

CREATE TABLE sensitive_information (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(50),
    credit_card_number VARCHAR(16)
);

CREATE TABLE masked_information (
    id INT PRIMARY KEY,
    masked_name VARCHAR(50),
    masked_email VARCHAR(50),
    masked_credit_card_number VARCHAR(16)
);

CREATE TABLE data_masking_rules (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(50),
    mask_type VARCHAR(50),
    mask_pattern VARCHAR(50)
);

CREATE TABLE Organizations (
    org_id UUID PRIMARY KEY,
    name VARCHAR(255),
    industry VARCHAR(100),
    masked_name VARCHAR(255)
);

CREATE TABLE Departments (
    dept_id UUID PRIMARY KEY,
    name VARCHAR(100),
    manager_id UUID,
    masked_name VARCHAR(100)
);

CREATE TABLE Salaries (
    salary_id UUID PRIMARY KEY,
    emp_id UUID,
    amount DECIMAL(10, 2),
    masked_amount DECIMAL(10, 2)
);

CREATE TABLE employees (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    job_title VARCHAR(50),
    salary DECIMAL(10,2)
);

CREATE TABLE departments (
    dept_id INT PRIMARY KEY,
    dept_name VARCHAR(50)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(50),
    phone VARCHAR(20)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    price DECIMAL(10,2),
    category VARCHAR(50)
);
