tables.table_schema:
CREATE TABLE retention_policy (
    id SERIAL PRIMARY KEY,
    policy_name VARCHAR(50) NOT NULL,
    retention_duration INT NOT NULL,
    is_active BOOLEAN NOT NULL
);

CREATE TABLE data_retention_policies (
    id INT,
    policy_name VARCHAR(255),
    retention_period INT
);

CREATE TABLE archived_data (
    archived_id INT,
    original_id INT,
    archived_date DATETIME,
    archived_data JSON
);

CREATE TABLE retention_policies (
    policy_id SERIAL PRIMARY KEY,
    policy_name VARCHAR(50) NOT NULL,
    retention_period INTERVAL NOT NULL
);

CREATE TABLE data_to_be_retained (
    data_id SERIAL PRIMARY KEY,
    data_type VARCHAR(50) NOT NULL,
    creation_date DATE NOT NULL,
    is_archived BOOLEAN DEFAULT FALSE
);

CREATE TABLE archived_data (
    archived_id SERIAL PRIMARY KEY,
    original_data_id INT REFERENCES data_to_be_retained(data_id),
    archive_date DATE NOT NULL
);

CREATE TABLE data_retention_policy (
    id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    retention_period INT,
    archival_location VARCHAR(100)
);

CREATE TABLE business (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    industry VARCHAR(50),
    contact_person VARCHAR(50)
);

CREATE TABLE data_record (
    id INT PRIMARY KEY,
    data_value TEXT,
    creation_date DATE,
    business_id INT
);

CREATE TABLE data_archive (
    id INT PRIMARY KEY,
    archive_date DATE,
    archived_data TEXT,
    retention_policy_id INT
);

CREATE TABLE retention_policy (
    id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    policy_description TEXT,
    retention_period INT
);

CREATE TABLE data_entry (
    entry_id UUID PRIMARY KEY,
    data_value JSONB,
    entry_date TIMESTAMP
);

CREATE TABLE data_archive (
    archive_id UUID PRIMARY KEY,
    archive_data JSONB,
    archive_date TIMESTAMP
);

CREATE TABLE deletion_log (
    log_id SERIAL PRIMARY KEY,
    deleted_entry_id UUID,
    deletion_date TIMESTAMP
);

CREATE TABLE retention_log (
    log_id SERIAL PRIMARY KEY,
    log_type VARCHAR(20),
    log_description TEXT,
    log_date TIMESTAMP
);
