tables.table_schema:
CREATE TABLE SecurityIncidents (
    IncidentID SERIAL PRIMARY KEY,
    IncidentType VARCHAR(255) NOT NULL,
    IncidentDate DATE NOT NULL,
    IncidentDetails TEXT
);

CREATE TABLE SecurityBreachDetails (
    BreachID SERIAL PRIMARY KEY,
    BreachType VARCHAR(255) NOT NULL,
    BreachDate DATE NOT NULL,
    BreachDetails TEXT
);

CREATE TABLE InvestigationTeam (
    TeamID SERIAL PRIMARY KEY,
    TeamName VARCHAR(255) NOT NULL,
    TeamLead VARCHAR(255) NOT NULL,
    TeamMembers TEXT[]
);

CREATE TABLE security_incidents (
    incident_id UUID PRIMARY KEY,
    incident_date DATE,
    location TEXT,
    incident_description TEXT
);

CREATE TABLE involved_parties (
    party_id UUID PRIMARY KEY,
    party_name TEXT,
    party_type TEXT,
    incident_id UUID REFERENCES security_incidents(incident_id)
);

CREATE TABLE evidence (
    evidence_id UUID PRIMARY KEY,
    incident_id UUID REFERENCES security_incidents(incident_id),
    evidence_description TEXT,
    evidence_type TEXT
);
