tables.table_schema:
CREATE TABLE MonitoringRules (
  rule_id INT PRIMARY KEY,
  rule_name VARCHAR(255) NOT NULL,
  rule_description TEXT
);

CREATE TABLE DetectedAttacks (
  attack_id INT PRIMARY KEY,
  attack_type VARCHAR(50) NOT NULL,
  attack_description TEXT,
  detection_time TIMESTAMP
);

CREATE TABLE monitoring_data (
    id UUID PRIMARY KEY,
    query_text STRING,
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE suspicious_queries (
    id UUID PRIMARY KEY,
    query_text STRING,
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE suspicious_patterns (
    id UUID PRIMARY KEY,
    pattern_name STRING,
    pattern_definition STRING
);

CREATE TABLE suspicious_users (
    id UUID PRIMARY KEY,
    user_id INT,
    detection_timestamp TIMESTAMP
);
