tables.table_schema:
CREATE TABLE encryption_keys (
    key_id UUID PRIMARY KEY,
    key_value TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT now()
);

CREATE TABLE encrypted_data (
    data_id UUID PRIMARY KEY,
    encrypted_value TEXT NOT NULL,
    key_id UUID REFERENCES encryption_keys(key_id),
    created_at TIMESTAMP DEFAULT now()
);

CREATE TABLE encryption_keys (
  key_id INT PRIMARY KEY,
  key_value VARCHAR(255) NOT NULL,
  created_at TIMESTAMP
);

CREATE TABLE key_users (
  user_id INT PRIMARY KEY,
  key_id INT,
  user_name VARCHAR(100),
  FOREIGN KEY (key_id) REFERENCES encryption_keys(key_id)
);

CREATE TABLE key_permissions (
  permission_id INT PRIMARY KEY,
  key_id INT,
  permission_type VARCHAR(50),
  FOREIGN KEY (key_id) REFERENCES encryption_keys(key_id)
);

CREATE TABLE encryption_keys (
    id INT PRIMARY KEY,
    key_name VARCHAR(50) NOT NULL,
    key_value VARCHAR(100) NOT NULL,
    key_type VARCHAR(20) NOT NULL
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    encryption_key_id INT NOT NULL,
    FOREIGN KEY (encryption_key_id) REFERENCES encryption_keys(id)
);

CREATE TABLE encrypted_data (
    data_id INT PRIMARY KEY,
    data_content TEXT NOT NULL,
    encryption_key_id INT NOT NULL,
    FOREIGN KEY (encryption_key_id) REFERENCES encryption_keys(id)
);

CREATE TABLE key_management (
    management_id INT PRIMARY KEY,
    key_type VARCHAR(20) NOT NULL,
    key_description TEXT,
    key_owner VARCHAR(50) NOT NULL
);

CREATE TABLE encryption_keys (
    key_id UUID PRIMARY KEY,
    key_value BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL,
    expiration_date TIMESTAMP
);

CREATE TABLE data_encryption (
    data_id UUID PRIMARY KEY,
    encrypted_data BYTEA NOT NULL,
    encryption_key_ref UUID NOT NULL,
    FOREIGN KEY (encryption_key_ref) REFERENCES encryption_keys(key_id)
);

CREATE TABLE key_management (
    key_manager_id UUID PRIMARY KEY,
    key_manager_name VARCHAR(100) NOT NULL
);
