tables.table_schema:
CREATE TABLE audit_log (
    log_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE compliance_status (
    status_id INT PRIMARY KEY,
    log_id INT,
    compliance VARCHAR(50),
    details TEXT
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) UNIQUE,
    role VARCHAR(20) NOT NULL
);

CREATE TABLE regulations (
    regulation_id SERIAL PRIMARY KEY,
    regulation_name VARCHAR(100) UNIQUE NOT NULL,
    organization VARCHAR(200) NOT NULL
);

CREATE TABLE user_info (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    role VARCHAR(50)
);

CREATE TABLE action_details (
    action_id INT PRIMARY KEY,
    action_description VARCHAR(255)
);

CREATE TABLE audit_logs (
  log_id INT PRIMARY KEY,
  user_id INT,
  action VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE user_accounts (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  created_at TIMESTAMP
);

CREATE TABLE compliance_requirements (
  requirement_id INT PRIMARY KEY,
  requirement_name VARCHAR(100),
  description TEXT
);

CREATE TABLE audit_log_details (
  detail_id INT PRIMARY KEY,
  log_id INT,
  detail_description TEXT
);
