tables.table_schema:
CREATE TABLE user_credentials (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  encrypted_password BYTEA NOT NULL
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    encrypted_password TEXT NOT NULL
);

CREATE TABLE encryption_algorithms (
    id INT PRIMARY KEY,
    algorithm_name VARCHAR(50),
    encryption_key BINARY(64)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    password_hash BINARY(64),
    encryption_algorithm_id INT
);

CREATE TABLE encryption_keys (
    key_id INT PRIMARY KEY,
    user_id INT,
    encryption_key BINARY(64)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE encryption_keys (
    key_id INT PRIMARY KEY,
    user_id INT,
    encryption_key VARCHAR(200) NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE password_policies (
    policy_id INT PRIMARY KEY,
    user_id INT,
    complexity_level VARCHAR(20) NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE user_accounts (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    password VARBINARY(100),
    encryption_key VARBINARY(50)
);

CREATE TABLE encryption_keys (
    user_id INT PRIMARY KEY,
    encryption_key VARBINARY(50)
);

CREATE TABLE password_history (
    user_id INT,
    password VARBINARY(100),
    date_changed TIMESTAMP
);

CREATE TABLE user_sessions (
    session_id VARCHAR(50) PRIMARY KEY,
    user_id INT,
    last_activity TIMESTAMP
);
