CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username TEXT NOT NULL,
    password BYTEA NOT NULL
);

CREATE TABLE encrypted_files (
  file_id INT PRIMARY KEY,
  file_name VARCHAR(255) NOT NULL,
  file_content BYTES NOT NULL,
  encryption_key VARCHAR(50) NOT NULL
);

CREATE TABLE file_metadata (
  metadata_id INT PRIMARY KEY,
  file_id INT REFERENCES encrypted_files(file_id) ON DELETE CASCADE,
  upload_date TIMESTAMP NOT NULL,
  file_size INT NOT NULL
);

CREATE TABLE access_logs (
  log_id INT PRIMARY KEY,
  file_id INT REFERENCES encrypted_files(file_id) ON DELETE CASCADE,
  access_date TIMESTAMP NOT NULL,
  user_id INT NOT NULL
);

CREATE TABLE Files (
    file_id INT PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    file_data BYTEA NOT NULL,
    encryption_key VARCHAR(50) NOT NULL
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE encrypted_files (
    file_id INT PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    file_data BYTEA NOT NULL,
    file_encryption_key VARCHAR(50) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE access_logs (
    access_log_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    access_status VARCHAR(10) NOT NULL,
    date_time TIMESTAMP NOT NULL
);
