tables.table_schema:
CREATE TABLE DataRetentionPolicy (
    id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    retention_duration INT,
    creation_date TIMESTAMP
);

CREATE TABLE retention_policies (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(255),
    retention_period INT
);

CREATE TABLE retention_policies (
   policy_id INT PRIMARY KEY,
   policy_name VARCHAR(255) NOT NULL,
   retention_days INT NOT NULL
);

CREATE TABLE retained_data (
   data_id INT PRIMARY KEY,
   data_content TEXT NOT NULL,
   created_date TIMESTAMP DEFAULT now()
);

CREATE TABLE data_categories (
   category_id INT PRIMARY KEY,
   category_name VARCHAR(255) NOT NULL
);

CREATE TABLE data_mapping (
   mapping_id INT PRIMARY KEY,
   data_id INT REFERENCES retained_data(data_id),
   category_id INT REFERENCES data_categories(category_id)
);

CREATE TABLE data_retention_policy (
  policy_id SERIAL PRIMARY KEY,
  policy_name VARCHAR(100) NOT NULL,
  retention_period INTERVAL NOT NULL
);

CREATE TABLE data_table (
  data_id SERIAL PRIMARY KEY,
  data_name VARCHAR(100) NOT NULL,
  data_content TEXT NOT NULL,
  creation_date TIMESTAMP NOT NULL
);

CREATE TABLE expired_data (
  expired_data_id SERIAL PRIMARY KEY,
  data_id INTEGER REFERENCES data_table(data_id),
  expired_date TIMESTAMP NOT NULL
);

CREATE TABLE data_category (
  category_id SERIAL PRIMARY KEY,
  category_name VARCHAR(100) NOT NULL
);

CREATE TABLE data_categorization (
  categorization_id SERIAL PRIMARY KEY,
  data_id INTEGER REFERENCES data_table(data_id),
  category_id INTEGER REFERENCES data_category(category_id)
);
