tables.table_schema:
CREATE TABLE vulnerability_scan_results (
    scan_id UUID PRIMARY KEY,
    vulnerability_name VARCHAR(255),
    vulnerability_severity VARCHAR(50),
    scan_date DATE
);
CREATE TABLE vulnerability_assessments (
    assessment_id UUID PRIMARY KEY,
    scan_id UUID REFERENCES vulnerability_scan_results(scan_id),
    assessment_status VARCHAR(50),
    assessment_date DATE
);

CREATE TABLE vulnerability_scan_results (
    scan_id INT PRIMARY KEY,
    scan_date TIMESTAMP NOT NULL,
    scan_result TEXT NOT NULL
);

CREATE TABLE vulnerabilities (
    vulnerability_id INT PRIMARY KEY,
    vulnerability_name VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);
