tables.table_schema:
CREATE TABLE biometric_authentication (
  biometric_id INT NOT NULL PRIMARY KEY,
  biometric_data JSONB NOT NULL,
  authentication_status BOOLEAN NOT NULL
);

CREATE TABLE biometric_data ( 
  id INT PRIMARY KEY, 
  biometric_type VARCHAR(50), 
  biometric_feature TEXT 
);

CREATE TABLE authentication_logs ( 
  log_id SERIAL PRIMARY KEY, 
  user_id INT, 
  authenticated_at TIMESTAMP,
  success BOOLEAN, 
  FOREIGN KEY (user_id) REFERENCES biometric_data(id) 
);

CREATE TABLE biometric_data (
    biometric_id INT PRIMARY KEY,
    biometric_type VARCHAR(50),
    biometric_data BLOB
);

CREATE TABLE user_data (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    user_bio_id INT,
    FOREIGN KEY (user_bio_id) REFERENCES biometric_data(biometric_id)
);

CREATE TABLE real_time_biometric (
    real_bio_id INT PRIMARY KEY,
    biometric_type VARCHAR(50),
    real_time_data BLOB
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL,
    full_name VARCHAR(100) NOT NULL
);

CREATE TABLE authentication_logs (
    log_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    authentication_result BOOLEAN NOT NULL,
    timestamp TIMESTAMP
);

CREATE TABLE comparison_results (
    result_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    comparison_result BOOLEAN NOT NULL,
    timestamp TIMESTAMP
);
