CREATE TABLE UserProfiles (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    biometric_data BLOB
);

CREATE TABLE BiometricCharacteristics (
    bio_id INT PRIMARY KEY,
    bio_type VARCHAR(50) NOT NULL,
    bio_description TEXT
);

CREATE TABLE AccessControl (
    access_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    bio_id INT NOT NULL,
    access_time TIMESTAMP
);

CREATE TABLE UserLogs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    log_description TEXT,
    log_time TIMESTAMP
);

CREATE TABLE biometric_profiles (
    id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    biometric_type VARCHAR(50) NOT NULL,
    biometric_data BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE users (
    id UUID PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE fingerprints (
    id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    fingerprint_data BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE iris_scans (
    id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    iris_data BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE face_scans (
    id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    face_data BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL
);
