tables.table_schema:
CREATE TABLE biometric_features (
    user_id UUID PRIMARY KEY,
    facial_pattern BYTEA,
    fingerprint_pattern BYTEA,
    voice_pattern BYTEA
);

CREATE TABLE facial_recognition_patterns (
    user_id UUID PRIMARY KEY,
    facial_data BYTEA
);

CREATE TABLE user_verification (
    user_id UUID PRIMARY KEY,
    is_verified BOOLEAN
);

CREATE TABLE facial_recognition_patterns (
    user_id INT PRIMARY KEY,
    face_feature BLOB,
    timestamp TIMESTAMP
);

CREATE TABLE user_identification (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL,
    enrollment_date DATE
);

CREATE TABLE user_verification (
    user_id INT PRIMARY KEY,
    verified BOOLEAN DEFAULT FALSE,
    verification_date TIMESTAMP
);

CREATE TABLE facial_recognition ( 
    user_id INT PRIMARY KEY, 
    facial_pattern BYTEA NOT NULL 
);

CREATE TABLE user_information ( 
    user_id INT PRIMARY KEY, 
    first_name VARCHAR(50) NOT NULL, 
    last_name VARCHAR(50) NOT NULL 
);

CREATE TABLE access_logs ( 
    access_id SERIAL PRIMARY KEY, 
    user_id INT NOT NULL, 
    access_time TIMESTAMP NOT NULL 
);

CREATE TABLE authentication_logs ( 
    log_id SERIAL PRIMARY KEY, 
    user_id INT NOT NULL, 
    authentication_time TIMESTAMP NOT NULL, 
    success BOOLEAN NOT NULL 
);

CREATE TABLE facial_recognition (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    facial_data BYTEA NOT NULL
);

CREATE TABLE user_logs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    action VARCHAR(50) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE access_control (
    access_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    access_level INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE images (
    image_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    image BYTEA NOT NULL,
    timestamp TIMESTAMP NOT NULL
);
