tables.table_schema:
CREATE TABLE BiometricData (
    user_id INT,
    biometric_data BYTEA,
    PRIMARY KEY (user_id)
);

CREATE TABLE AccessLevels (
    user_id INT,
    access_level VARCHAR(50),
    FOREIGN KEY (user_id) REFERENCES BiometricData(user_id) ON DELETE CASCADE
);

CREATE TABLE biometric_data (
    user_id INT PRIMARY KEY,
    fingerprint_data VARCHAR(255),
    face_scan_data VARCHAR(255),
    voice_print_data VARCHAR(255)
);

CREATE TABLE access_levels (
    user_id INT PRIMARY KEY,
    security_clearance INT,
    access_type VARCHAR(50)
);

CREATE TABLE access_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    action VARCHAR(50)
);

CREATE TABLE permissions (
    permission_id SERIAL PRIMARY KEY,
    access_type VARCHAR(50),
    level_required INT
);
