tables.table_schema:
CREATE TABLE biometric_data (
    id INT PRIMARY KEY,
    user_id INT,
    biometric_type VARCHAR(20),
    biometric_data BLOB
);

CREATE TABLE authentication_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    login_time TIMESTAMP,
    logout_time TIMESTAMP
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    login_timestamp TIMESTAMP,
    logout_timestamp TIMESTAMP
);

CREATE TABLE biometric_data (
    user_id INT PRIMARY KEY,
    fingerprint_data BYTEA,
    face_data BYTEA,
    iris_data BYTEA
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    access_time TIMESTAMP,
    access_type VARCHAR(20)
);

CREATE TABLE authentication_history (
    log_id INT PRIMARY KEY,
    user_id INT,
    authentication_result BOOLEAN,
    authentication_time TIMESTAMP
);

CREATE TABLE user_info (
    user_id UUID PRIMARY KEY,
    username TEXT NOT NULL,
    password TEXT NOT NULL,
    email TEXT NOT NULL
);

CREATE TABLE access_logs (
    log_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    access_time TIMESTAMP NOT NULL,
    location TEXT NOT NULL
);

CREATE TABLE security_settings (
    user_id UUID PRIMARY KEY,
    enable_biometrics BOOLEAN NOT NULL,
    enable_two_factor BOOLEAN NOT NULL
);
