CREATE TABLE authorized_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_ip_address VARCHAR(15)
);

CREATE TABLE authorized_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_ip_address VARCHAR(15),
    device_config JSONB
);

CREATE TABLE user_permissions (
    user_id INT PRIMARY KEY,
    device_id INT,
    permission_level INT,
    FOREIGN KEY (device_id) REFERENCES authorized_devices(device_id)
);

CREATE TABLE authorized_devices (
 device_id INT PRIMARY KEY,
 device_name VARCHAR(50) NOT NULL,
 device_type VARCHAR(50) NOT NULL,
 ip_address VARCHAR(15) NOT NULL
);

CREATE TABLE device_configurations (
 config_id INT PRIMARY KEY,
 device_id INT REFERENCES authorized_devices(device_id),
 config_name VARCHAR(50) NOT NULL,
 config_value TEXT NOT NULL
);

CREATE TABLE users (
 user_id INT PRIMARY KEY,
 username VARCHAR(50) NOT NULL UNIQUE,
 password VARCHAR(255) NOT NULL
);

CREATE TABLE user_roles (
 role_id INT PRIMARY KEY,
 user_id INT REFERENCES users(user_id),
 role_name VARCHAR(50) NOT NULL
);

CREATE TABLE authorized_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(50),
    configuration TEXT
);

CREATE TABLE device_types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50),
    description TEXT
);

CREATE TABLE network_configurations (
    network_id INT PRIMARY KEY,
    network_name VARCHAR(50),
    network_type VARCHAR(50),
    network_address VARCHAR(50)
);
