tables.table_schema:
CREATE TABLE vpn_servers (
    id UUID PRIMARY KEY,
    server_name VARCHAR,
    server_ip VARCHAR,
    location VARCHAR
);

CREATE TABLE VPN_Servers (
    server_id INT PRIMARY KEY,
    server_name VARCHAR(255) NOT NULL,
    server_ip VARCHAR(15) NOT NULL,
    max_connections INT
);

CREATE TABLE VPN_Configurations (
    config_id INT PRIMARY KEY,
    server_id INT REFERENCES VPN_Servers(server_id),
    client_id INT REFERENCES VPN_Clients(client_id),
    active_connection BOOLEAN
);

CREATE TABLE VPN_Users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL,
    email VARCHAR(100)
);

CREATE TABLE VPN_Connection_Logs (
    log_id SERIAL PRIMARY KEY,
    user_id INTEGER NOT NULL,
    connection_timestamp TIMESTAMP NOT NULL,
    disconnect_timestamp TIMESTAMP
);

CREATE TABLE VPN_Servers (
    server_id SERIAL PRIMARY KEY,
    server_name VARCHAR(50) NOT NULL,
    ip_address VARCHAR(15) NOT NULL,
    status BOOLEAN NOT NULL
);

CREATE TABLE VPN_Settings (
    setting_id SERIAL PRIMARY KEY,
    setting_name VARCHAR(50) NOT NULL,
    setting_value TEXT
);
