tables.table_schema:
CREATE TABLE vpn_tunnel_encryption_keys (
    key_id UUID PRIMARY KEY,
    user_id UUID,
    key_data BYTEA
);

CREATE TABLE vpn_tunnel_keys (
    id INT PRIMARY KEY,
    key_name VARCHAR(50),
    key_value VARCHAR(100),
    created_at TIMESTAMP
);

CREATE TABLE VPNUsers (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50) UNIQUE NOT NULL,
  password VARCHAR(100) NOT NULL
);

CREATE TABLE VPNServers (
  server_id SERIAL PRIMARY KEY,
  server_name VARCHAR(50) UNIQUE NOT NULL,
  server_ip VARCHAR(15) NOT NULL
);

CREATE TABLE VPNKeys (
  key_id SERIAL PRIMARY KEY,
  key_value TEXT NOT NULL,
  user_id INT REFERENCES VPNUsers(user_id),
  server_id INT REFERENCES VPNServers(server_id)
);

CREATE TABLE encryption_keys (
    key_id INT PRIMARY KEY,
    key_name VARCHAR(50),
    key_value BLOB
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    os_version VARCHAR(20)
);

CREATE TABLE vpn_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    password_hash VARCHAR(100)
);

CREATE TABLE vpn_groups (
    group_id INT PRIMARY KEY,
    group_name VARCHAR(50) UNIQUE,
    group_description TEXT
);
