tables.table_schema:
CREATE TABLE ErrorMessages (
    error_id INT PRIMARY KEY,
    error_message TEXT,
    error_timestamp TIMESTAMP
);

CREATE TABLE AlertMessages (
    alert_id INT PRIMARY KEY,
    alert_message TEXT,
    alert_level INT,
    alert_timestamp TIMESTAMP
);

CREATE TABLE errors (
    id SERIAL PRIMARY KEY,
    vpn_service_id INT,
    message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE vpn_services (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100),
    location VARCHAR(100)
);

CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    last_login TIMESTAMP
);
