tables.table_schema:
CREATE TABLE vpn_clients (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(255),
    client_version VARCHAR(50),
    last_updated TIMESTAMP
);

CREATE TABLE vpn_clients (
    client_id UUID PRIMARY KEY,
    client_name VARCHAR(50),
    client_version VARCHAR(20),
    last_updated TIMESTAMP
);

CREATE TABLE patches (
    patch_id UUID PRIMARY KEY,
    patch_name VARCHAR(50),
    patch_details TEXT,
    release_date DATE
);

CREATE TABLE updates (
    update_id UUID PRIMARY KEY,
    update_name VARCHAR(50),
    update_details TEXT,
    update_date DATE
);

CREATE TABLE clients_updates (
    client_id UUID,
    update_id UUID,
    FOREIGN KEY (client_id) REFERENCES vpn_clients(client_id),
    FOREIGN KEY (update_id) REFERENCES updates(update_id)
);

CREATE TABLE clients (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(255) NOT NULL,
    client_version VARCHAR(50) NOT NULL,
    last_update DATETIME
);
