tables.table_schema:
CREATE TABLE customer_info (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    address VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    customer_id INT REFERENCES customers(customer_id),
    transaction_amount DECIMAL(10, 2),
    transaction_date TIMESTAMP
);

CREATE TABLE suspicious_activities (
    activity_id INT PRIMARY KEY,
    customer_id INT REFERENCES customers(customer_id),
    activity_description TEXT,
    activity_date TIMESTAMP
);

CREATE TABLE activity_logs (
    log_id INT PRIMARY KEY,
    activity_id INT REFERENCES fraudulent_activity(activity_id),
    log_description TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE flagged_customers (
    customer_id INT PRIMARY KEY REFERENCES customer_information(customer_id),
    flag_reason TEXT,
    flag_timestamp TIMESTAMP
);

CREATE TABLE investigation_team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    team_lead VARCHAR(50),
    team_size INT
);
