tables.table_schema:
CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  password VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL
);

CREATE TABLE transactions (
  transaction_id INT PRIMARY KEY,
  user_id INT NOT NULL,
  amount DECIMAL(10, 2) NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE law_enforcement (
  agency_id INT PRIMARY KEY,
  agency_name VARCHAR(100) NOT NULL,
  contact_name VARCHAR(50) NOT NULL,
  contact_phone VARCHAR(15) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE reports (
    report_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    report_text TEXT,
    report_date DATE
);

CREATE TABLE investigations (
    investigation_id INT PRIMARY KEY,
    report_id INT REFERENCES reports(report_id),
    investigator_name VARCHAR(100) NOT NULL,
    investigation_date DATE
);

CREATE TABLE law_enforcement_agencies (
  id INTEGER,
  agency_name VARCHAR(255),
  location VARCHAR(255) 
);

CREATE TABLE identity_theft_cases (
  case_id INTEGER,
  victim_name VARCHAR(255),
  perpetrator_name VARCHAR(255),
  theft_description TEXT 
);

CREATE TABLE stolen_credentials (
  credential_id INTEGER,
  username VARCHAR(255),
  password_hash VARCHAR(255),
  account_type VARCHAR(50) 
);

CREATE TABLE data_sharing_logs (
  log_id INTEGER,
  share_date DATE,
  shared_table VARCHAR(255) 
);

CREATE TABLE investigation_team (
  team_id INTEGER,
  team_name VARCHAR(255),
  team_lead VARCHAR(255),
  members TEXT 
);
