CREATE TABLE smart_contracts (
    contract_id INT PRIMARY KEY,
    contract_code TEXT,
    metadata JSON
);

CREATE TABLE deployed_contracts (
    contract_id INT PRIMARY KEY,
    deployed_by VARCHAR(255),
    deployment_time TIMESTAMP,
    execution_status BOOLEAN
);

CREATE TABLE smart_contracts (
    contract_id INT PRIMARY KEY,
    contract_code TEXT,
    contract_metadata JSON
);

CREATE TABLE contract_deployments (
    deployment_id INT PRIMARY KEY,
    contract_id INT REFERENCES smart_contracts(contract_id),
    deployment_date TIMESTAMP,
    deployment_status VARCHAR(20)
);

CREATE TABLE smart_contracts (
    contract_id INT PRIMARY KEY,
    contract_code TEXT,
    metadata JSON
);

CREATE TABLE contract_owners (
    owner_id INT PRIMARY KEY,
    contract_id INT FOREIGN KEY REFERENCES smart_contracts(contract_id),
    owner_name TEXT,
    ownership_percentage FLOAT
);

CREATE TABLE contract_audit_logs (
    log_id INT PRIMARY KEY,
    contract_id INT FOREIGN KEY REFERENCES smart_contracts(contract_id),
    action TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE contract_versions (
    version_id INT PRIMARY KEY,
    contract_id INT FOREIGN KEY REFERENCES smart_contracts(contract_id),
    version_code TEXT,
    created_date DATE
);

CREATE TABLE smart_contracts (
    contract_id INT PRIMARY KEY,
    contract_code TEXT,
    contract_metadata JSONB
);
