tables.table_schema:
CREATE TABLE compliance_requirements (
    id INT PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulation_description TEXT
);

CREATE TABLE compliance_logs (
    log_id INT PRIMARY KEY,
    transaction_id INT,
    compliance_status BOOLEAN,
    compliance_details TEXT
);

CREATE TABLE regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(100),
  description TEXT,
  regulatory_body VARCHAR(100)
);

CREATE TABLE blockchain_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_hash VARCHAR(255),
    transaction_amount DECIMAL,
    transaction_timestamp TIMESTAMP
);

CREATE TABLE wallet_accounts (
    account_id INT PRIMARY KEY,
    account_address VARCHAR(255),
    account_balance DECIMAL
);

CREATE TABLE regulatory_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255),
    agency_contact_person VARCHAR(255),
    agency_contact_email VARCHAR(255)
);

CREATE TABLE compliance_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    report_text TEXT
);

CREATE TABLE compliance_history (
    history_id INT PRIMARY KEY,
    compliance_status VARCHAR(255),
    compliance_date DATE,
    findings TEXT
);
