tables.table_schema:
CREATE TABLE blockchain_network (
    network_id UUID PRIMARY KEY,
    network_name VARCHAR(100),
    network_type ENUM('public', 'private'),
    creation_date TIMESTAMP
);

CREATE TABLE dapp_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    last_login TIMESTAMP
);

CREATE TABLE dapp_contracts (
    contract_id INT PRIMARY KEY,
    contract_name VARCHAR(100),
    deploy_date DATE
);

CREATE TABLE dapp_blocks (
    block_id INT PRIMARY KEY,
    block_hash VARCHAR(256),
    block_timestamp TIMESTAMP
);
