tables.table_schema:
CREATE TABLE metadata_table (
  id SERIAL PRIMARY KEY,
  token_id TEXT NOT NULL,
  owner_address TEXT NOT NULL,
  metadata_json JSONB
);

CREATE TABLE nft_metadata (
    nft_id INT PRIMARY KEY,
    token_name VARCHAR(255),
    owner_address VARCHAR(255),
    description TEXT
);

CREATE TABLE nft_owners (
    owner_id SERIAL PRIMARY KEY,
    owner_address VARCHAR(255),
    total_nfts_owned INT
);

CREATE TABLE nft_ownership (
   id SERIAL PRIMARY KEY,
   token_id VARCHAR(255) NOT NULL,
   owner_address VARCHAR(255) NOT NULL,
   timestamp TIMESTAMP NOT NULL
);

CREATE TABLE nft_blockchain (
   block_id SERIAL PRIMARY KEY,
   block_hash VARCHAR(255) NOT NULL,
   previous_block_hash VARCHAR(255),
   block_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE NFT_Metadata (
    token_id UUID PRIMARY KEY,
    token_name VARCHAR(255),
    token_description TEXT,
    blockchain_id UUID
);

CREATE TABLE Blockchain_Info (
    blockchain_id UUID PRIMARY KEY,
    blockchain_name VARCHAR(100),
    blockchain_network VARCHAR(50)
);

CREATE TABLE Owners (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR(100),
    owner_wallet_address VARCHAR(255)
);

CREATE TABLE NFT_Ownership (
    ownership_id UUID PRIMARY KEY,
    token_id UUID,
    owner_id UUID,
    ownership_date TIMESTAMP
);
