CREATE TABLE UserCredentials (
    user_id INT PRIMARY KEY,
    username VARCHAR(255) NOT NULL,
    password_hash VARCHAR(255) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL,
    last_login TIMESTAMP
);

CREATE TABLE user_secret (
    user_id INT PRIMARY KEY,
    secret_question VARCHAR(100),
    secret_answer VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE user_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_roles_mappings (
    user_id INT REFERENCES users(user_id),
    role_id INT REFERENCES user_roles(role_id)
);

CREATE TABLE user_permissions_mappings (
    user_id INT REFERENCES users(user_id),
    permission_id INT REFERENCES user_permissions(permission_id)
);
