tables.table_schema:
CREATE TABLE successful_authentications (id INT PRIMARY KEY,
user_id INT,
username VARCHAR(50),
authentication_time TIMESTAMP,
authentication_method VARCHAR(50)
);

CREATE TABLE authentication_attempts (
    id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    success BOOLEAN
);

CREATE TABLE authentication_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    action VARCHAR(255)
);

CREATE TABLE authenticated_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    password_hash VARCHAR(255),
    authentication_time TIMESTAMP
);

CREATE TABLE authentication_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    login_time TIMESTAMP,
    login_status VARCHAR(50)
);

CREATE TABLE authentication_failures (
    failure_id INT PRIMARY KEY,
    user_id INT,
    failure_time TIMESTAMP,
    failure_reason VARCHAR(255)
);

CREATE TABLE successful_auth_attempts (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    successful_login_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE failed_auth_attempts (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    failed_login_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE account_lockouts (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    lockout_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE password_changes (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_changed_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);
