tables.table_schema:
CREATE TABLE encryption_keys (
    id SERIAL PRIMARY KEY,
    key_name VARCHAR(50) NOT NULL,
    key_value TEXT NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE authentication_components (
    id SERIAL PRIMARY KEY,
    component_name VARCHAR(50) NOT NULL,
    component_type VARCHAR(50) NOT NULL,
    component_key_id INT NOT NULL,
    FOREIGN KEY (component_key_id) REFERENCES encryption_keys(id)
);

Create Table encryption_keys (
    key_id UUID PRIMARY KEY,
    key_value BYTEA,
    created_at TIMESTAMP,
    updated_at TIMESTAMP
);

CREATE TABLE encryption_keys (
    key_id INT PRIMARY KEY,
    key_value VARCHAR(255) NOT NULL,
    creation_date TIMESTAMP NOT NULL,
    expiration_date TIMESTAMP
);

CREATE TABLE authentication_components (
    component_id INT PRIMARY KEY,
    component_name VARCHAR(50) NOT NULL,
    component_type VARCHAR(50) NOT NULL,
    last_accessed TIMESTAMP
);

CREATE TABLE key_rotation_logs (
    rotation_id INT PRIMARY KEY,
    key_id INT NOT NULL,
    rotation_date TIMESTAMP NOT NULL,
    FOREIGN KEY (key_id) REFERENCES encryption_keys(key_id)
);

CREATE TABLE encryption_keys (
  key_id SERIAL PRIMARY KEY,
  key_name VARCHAR(50) NOT NULL,
  key_value TEXT NOT NULL
);

CREATE TABLE users (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50) UNIQUE NOT NULL,
  password_hash TEXT NOT NULL
);

CREATE TABLE user_sessions (
  session_id SERIAL PRIMARY KEY,
  user_id INT NOT NULL,
  session_token TEXT NOT NULL,
  expiration_time TIMESTAMP NOT NULL
);

CREATE TABLE key_permissions (
  permission_id SERIAL PRIMARY KEY,
  key_id INT NOT NULL,
  user_id INT NOT NULL
);

CREATE TABLE audit_log (
  log_id SERIAL PRIMARY KEY,
  log_message TEXT NOT NULL,
  timestamp TIMESTAMP NOT NULL
);
