CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    notification_preference BOOLEAN,
    display_preference VARCHAR
);

CREATE TABLE notification_preferences (
    user_id INT,
    notification_type VARCHAR,
    PRIMARY KEY (user_id, notification_type)
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    notification_settings JSON,
    display_settings JSON
);

CREATE TABLE notification_preferences (
    user_id INT PRIMARY KEY,
    email_notifications BOOLEAN,
    push_notifications BOOLEAN,
    in_app_notifications BOOLEAN
);

CREATE TABLE display_preferences (
    user_id INT PRIMARY KEY,
    theme VARCHAR(50),
    font_size INT,
    language VARCHAR(50)
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    notification_pref BOOLEAN,
    display_pref VARCHAR
);

CREATE TABLE notification_preferences (
    preference_id INT PRIMARY KEY,
    user_id INT,
    email_notifications BOOLEAN,
    push_notifications BOOLEAN
);

CREATE TABLE display_preferences (
    user_id INT PRIMARY KEY,
    dark_mode BOOLEAN,
    font_size VARCHAR
);

CREATE TABLE community_platform (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR UNIQUE,
    total_users INT
);

CREATE TABLE notification_preferences (
  user_id INT NOT NULL,
  email_enabled BOOLEAN DEFAULT TRUE,
  push_notification_enabled BOOLEAN DEFAULT FALSE,
  sound_notification_enabled BOOLEAN DEFAULT TRUE
);

CREATE TABLE display_preferences (
  user_id INT NOT NULL,
  dark_mode_enabled BOOLEAN DEFAULT FALSE,
  font_size INT DEFAULT 16,
  theme VARCHAR(255) DEFAULT 'light'
);

CREATE TABLE user_preferences (
  user_id INT NOT NULL,
  language VARCHAR(255) DEFAULT 'english',
  timezone VARCHAR(255) DEFAULT 'UTC'
);

CREATE TABLE user_settings (
  user_id INT NOT NULL,
  auto_play_videos BOOLEAN DEFAULT TRUE,
  show_online_status BOOLEAN DEFAULT TRUE,
  display_name VARCHAR(255)
);

CREATE TABLE community_preferences (
  community_id INT NOT NULL,
  allow_comments BOOLEAN DEFAULT TRUE,
  allow_sharing BOOLEAN DEFAULT TRUE,
  enable_recommendations BOOLEAN DEFAULT TRUE
);
